#!/bin/bash

export PROFILE=${1:-"dev"}
export STARKNET_RPC=${2:-"http://127.0.0.1:5050/"}


TARGET="target/${PROFILE}"
OUT="out/$PROFILE"
GENESIS_TEMPLATE=genesis_template.json
GENESIS_OUT="$OUT/genesis.json"
KATANA_LOG="$OUT/katana.log"
KATANA_DB="$OUT/katana_db"
KATANA_DB_ZIP="$OUT/katana_db.zip"
MANIFEST="manifests/$PROFILE/deployment/manifest.json"
TYPESCRIPT="$OUT/typescript"
TORII_DB="$OUT/torii.sqlite"
TORII_DB_ZIP="$OUT/torii.sqlite.zip"
TORII_LOG="$OUT/torii.log"
DEPLOY_SCARB="Scarb_deploy.toml"

check_needed_commands() {
  commands=("jq" "katana" "starkli" "zip" "sozo" "sqlite3")

  for cmd in "${commands[@]}"; do
      if ! command -v $cmd &> /dev/null; then
          echo "Error: $cmd is not available. The scripts need the following commands: ${commands[*]}"
          exit 1
      fi
  done

}


start_katana() {
    echo "start_katana"

    pkill -f katana

    mkdir -p $OUT

    rm -rf $KATANA_DB

    # Start Katana
    katana \
      --genesis $GENESIS_TEMPLATE \
      --invoke-max-steps 4294967295 \
      --disable-fee \
      --db-dir $KATANA_DB \
      --allowed-origins "*" \
     > $KATANA_LOG 2>&1 &

    # Wait for logfile to exist and not be empty
    while ! test -s $KATANA_LOG; do
     sleep 1
    done
}


start_torii() {
    echo "start_torii"

    pkill -f torii
    torii \
      --world $WORLD \
      --rpc $STARKNET_RPC \
      --database $TORII_DB \
      --events-chunk-size 10000 \
      --allowed-origins "*" \
     > $TORII_LOG 2>&1 &
}

sozo_rebuild() {
    echo "sozo_rebuild"
    rm -rf $TYPESCRIPT
    mkdir -p $TYPESCRIPT

    # Clear the target
    rm -rf $TARGET

    sozo clean
    sozo \
        --profile $PROFILE \
        --manifest-path $DEPLOY_SCARB \
        build \
        --typescript \
        --bindings-output $OUT
}



sozo_migrate() {
    echo "sozo_migrate"

    ## Sozo migrate plan
    sozo \
        --profile $PROFILE \
        --manifest-path $DEPLOY_SCARB \
        migrate \
        plan

    ## Sozo migrate apply
    sozo \
        --profile $PROFILE \
        --manifest-path $DEPLOY_SCARB \
        migrate \
        apply

    sleep 1
}


grant_write_permissions() {
    echo "grant_write_permissions"
    local models=$(jq -r '.models[] | select(.kind == "DojoModel") | .tag' $MANIFEST)

    for model in ${models[@]}; do
        sozo --manifest-path $DEPLOY_SCARB --profile $PROFILE auth grant --wait writer model:$model,pixelaw-actions
    done
}


init_actions() {
    echo "init_actions"
    local actions=$(jq -r '.contracts[] | select(.kind == "DojoContract") | .tag' $MANIFEST)

    for action in ${actions[@]}; do
        sozo --manifest-path $DEPLOY_SCARB --profile $PROFILE execute --wait $action init
    done
}


prepare_genesis() {
  echo "prepare_genesis"

  # Copy the template to the output file
  cat "$GENESIS_TEMPLATE" > "$GENESIS_OUT"

  last_block=$(starkli block)

  jq \
    --arg block_number "$(echo $last_block | jq -r '.block_number')" \
    --arg parent_hash "$(echo $last_block | jq -r '.parent_hash')" \
    --arg timestamp "$(echo $last_block | jq -r '.timestamp')" \
    '.parentHash = $parent_hash | .timestamp = ($timestamp | tonumber) | .number = ($block_number | tonumber)' \
    "$GENESIS_OUT" > temp.json \
    && mv temp.json "$GENESIS_OUT"
}


wait_for_torii_writing() {
  echo "wait_for_torii_writing"

  prev_size=$(du -b "$TORII_LOG" | cut -f1)

  while true; do
      sleep 5
      new_size=$(du -b "$TORII_LOG" | cut -f1)
      if [ $new_size -eq $prev_size ]; then
          break
      else
          prev_size=$new_size
      fi
  done
}

zip_databases() {

  echo "zip_databases"

  zip -1 -r $KATANA_DB_ZIP $KATANA_DB

  zip -1 $TORII_DB_ZIP $TORII_DB


}

patch_torii_db() {
  echo "patch_torii_db"
  sqlite3 $TORII_DB  "UPDATE contracts SET head = 0;"

}

kill_katana_torii() {
  pkill -f katana
  pkill -f torii
}

